    private void btnCalculaBolsaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculaBolsaActionPerformed
        
        Integer qtdFilhos0a2anos = Integer.valueOf(txtFilhos0a2.getText());
        Integer qtdFilhos3a7anos = Integer.valueOf(txtFilhos3a7.getText());
        Integer qtdFilhos8a15anos = Integer.valueOf(txtFilhos8a15.getText());
        
        Double valorBolsa = qtdFilhos0a2anos * 15.50;
        valorBolsa += qtdFilhos3a7anos * 10.00;
        valorBolsa += qtdFilhos8a15anos * 5.50;
        
        lblBolsa.setText(String.format("Sua bolsa kids ser√° de R$%.2f", valorBolsa));
    }//GEN-LAST:event_btnCalculaBolsaActionPerformed

