    private void btnCalculaIMCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculaIMCActionPerformed
        
        Double peso = Double.valueOf(txtPeso.getText().replaceAll(",", "."));
        Double altura = Double.valueOf(txtAltura.getText().replaceAll(",", "."));
        
        Double imc = peso / (altura * altura);
        
        lblIMC.setText(String.format("Seu imc Ã© %.1f", imc));
    }//GEN-LAST:event_btnCalculaIMCActionPerformed

